/* keep everything above the graphics */
* {
  position: relative;
  z-index: 1;
}

.section-container {
  /* Defined in Site CSS */
  display: grid;
  gap: 3.5rem;
  max-width: var(--inner-max-width);
  padding-block: 5rem;
  position: relative;
}

hr {
  border-color: #c6ccd2;
  margin-bottom: 3rem;
}

.headline {
  color: var(--color-heading);
  max-width: 800px;
  margin: 0 auto;
  font-size: clamp(2rem, 10vw, 5rem);
  line-height: 1;
  letter-spacing: -0.04rem;
  font-weight: 700;
  text-align: center;
  text-wrap: balance;
}

form {
  display: flex;
  flex-wrap: wrap;
  gap: 0.3rem;
  max-width: 400px;
  justify-self: center;

  label {
    flex: 1;

    input {
      width: 100%;
      padding: 0.6rem 0.75rem;
      outline-color: transparent;
      transition: 0.1s outline-color;
      border: 1.5px solid var(--border-color, #eee);
      font-weight: 300;
      border-radius: 0.25rem;

      &:focus {
        outline-color: var(--color-accent, rebeccapurple);
      }
    }
  }

  .button {
    /* Defined in Site CSS */
  }
}

.message {
  text-align: center;
  background: var(--background, white);
  padding: 1rem;
  box-shadow: 1px 1px 10px 5px rgba(0, 0, 0, 0.1);
  border-radius: 0.25rem;

  &.error {
    border: 2px solid red;
  }
}

.graphic {
  position: absolute;
  z-index: 0;

  &.left {
    width: 12%;
    bottom: 20%;
    left: 10%;
    animation: left-float 8s infinite alternate;
  }

  &.right {
    width: 15%;
    bottom: 60%;
    right: 10%;
    animation: right-float 8s infinite alternate;
  }

  @media (prefers-reduced-motion) {
    animation: none;
  }
}

@keyframes left-float {
  to {
    rotate: 5deg;
    transform: translateX(20px);
  }
}

@keyframes right-float {
  to {
    rotate: -20deg;
    transform: translateX(-20px);
  }
}

